mapper.update:
UPDATE D_DCM_GET_LOG SET
ITIME = #{iTime, jdbcType=TIMESTAMP},
UTIME = #{uTime, jdbcType=TIMESTAMP}
WHERE TYPE = #{type, jdbcType=DECIMAL}

mapper.insert:
INSERT INTO D_DCM_APPLY_M(BUSNO, DCMNO, BILLNO, STATUS, CREATETIME, LASTTIME) VALUES(#{busno, jdbcType=VARCHAR}, #{dcmno, jdbcType=VARCHAR}, #{billno, jdbcType=VARCHAR}, #{status, jdbcType=DECIMAL}, #{createTime, jdbcType=TIMESTAMP}, #{lastTime, jdbcType=TIMESTAMP})
INSERT INTO D_DCM_APPLY_C (BILLNO, ROWID, WAREID, WAREQTY, PURPRICE)
SELECT #{item.billno, jdbcType=VARCHAR}, #{item.rowid, jdbcType=VARCHAR}, #{item.wareid, jdbcType=VARCHAR}, #{item.wareqty, jdbcType=VARCHAR}, #{item.purprice, jdbcType=VARCHAR}

INSERT INTO D_DCM_RETURN_M(BUSNO, DCMNO, BILLNO, STATUS, CREATETIME, LASTTIME) VALUES(#{busno, jdbcType=VARCHAR}, #{dcmno, jdbcType=VARCHAR}, #{billno, jdbcType=VARCHAR}, #{status, jdbcType=DECIMAL}, #{createTime, jdbcType=TIMESTAMP}, #{lastTime, jdbcType=TIMESTAMP})
INSERT INTO D_DCM_RETURN_C(BILLNO, ROWID, WAREID, WAREQTY, PURPRICE)
SELECT #{item.billno, jdbcType=VARCHAR}, #{item.rowid, jdbcType=VARCHAR}, #{item.wareid, jdbcType=VARCHAR}, #{item.wareqty, jdbcType=DECIMAL}, #{item.purprice, jdbcType=DECIMAL}

INSERT INTO D_DCM_SALE_M(BUSNO, DCMNO, BILLNO, CREATETIME, LASTTIME) VALUES(#{busno, jdbcType=VARCHAR}, #{dcmno, jdbcType=VARCHAR}, #{billno, jdbcType=VARCHAR}, #{createTime, jdbcType=TIMESTAMP}, #{lastTime, jdbcType=TIMESTAMP})
INSERT INTO D_DCM_SALE_C(BILLNO, ROWID, WAREID, WAREQTY, SALEPRICE)
SELECT #{item.billno, jdbcType=VARCHAR}, #{item.rowid, jdbcType=VARCHAR}, #{item.wareid, jdbcType=VARCHAR}, #{item.wareqty, jdbcType=DECIMAL}, #{item.salePrice, jdbcType=DECIMAL}

mapper.select:
SELECT
FROM D_DCM_APPLY_M M LEFT JOIN D_DCM_APPLY_C C ON M.BILLNO = C.BILLNO WHERE M.STATUS = 0
AND M.LASTTIME > #{timestamp, jdbcType=TIMESTAMP}

SELECT
FROM D_DCM_RETURN_M M LEFT JOIN D_DCM_RETURN_C C ON M.BILLNO = C.BILLNO WHERE M.STATUS = 0
AND M.LASTTIME > #{timestamp, jdbcType=TIMESTAMP}

SELECT
FROM D_DCM_SALE_M M LEFT JOIN D_DCM_SALE_C C ON M.BILLNO = C.BILLNO
WHERE M.LASTTIME > #{timestamp, jdbcType=TIMESTAMP}

mapper.update:
BEGIN

    UPDATE D_DCM_APPLY_M SET
